mapper.select:
select 

from qrtz_fired_triggers
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and ENTRY_ID = #{entryId,jdbcType=VARCHAR}

mapper.delete:
delete from qrtz_fired_triggers
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and ENTRY_ID = #{entryId,jdbcType=VARCHAR}

mapper.insert:
insert into qrtz_fired_triggers (ENTRY_ID, `TRIGGER_NAME`, TRIGGER_GROUP, 
  INSTANCE_NAME, FIRED_TIME, SCHED_TIME, 
  PRIORITY, `STATE`, JOB_NAME, 
  JOB_GROUP, IS_NONCONCURRENT, REQUESTS_RECOVERY
  )
values (#{entryId,jdbcType=VARCHAR}, #{triggerName,jdbcType=VARCHAR}, #{triggerGroup,jdbcType=VARCHAR}, 
  #{instanceName,jdbcType=VARCHAR}, #{firedTime,jdbcType=BIGINT}, #{schedTime,jdbcType=BIGINT}, 
  #{priority,jdbcType=INTEGER}, #{state,jdbcType=VARCHAR}, #{jobName,jdbcType=VARCHAR}, 
  #{jobGroup,jdbcType=VARCHAR}, #{isNonconcurrent,jdbcType=VARCHAR}, #{requestsRecovery,jdbcType=VARCHAR}
  )
