mapper.select:
select 

,

from qrtz_job_details
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}

mapper.delete:
delete from qrtz_job_details
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}

mapper.insert:
insert into qrtz_job_details (JOB_NAME, JOB_GROUP, DESCRIPTION, 
  JOB_CLASS_NAME, IS_DURABLE, IS_NONCONCURRENT, 
  IS_UPDATE_DATA, REQUESTS_RECOVERY, JOB_DATA
  )
values (#{jobName,jdbcType=VARCHAR}, #{jobGroup,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, 
  #{jobClassName,jdbcType=VARCHAR}, #{isDurable,jdbcType=VARCHAR}, #{isNonconcurrent,jdbcType=VARCHAR}, 
  #{isUpdateData,jdbcType=VARCHAR}, #{requestsRecovery,jdbcType=VARCHAR}, #{jobData,jdbcType=BLOB}
  )

mapper.update:
update qrtz_job_details
set DESCRIPTION = #{description,jdbcType=VARCHAR},
  JOB_CLASS_NAME = #{jobClassName,jdbcType=VARCHAR},
  IS_DURABLE = #{isDurable,jdbcType=VARCHAR},
  IS_NONCONCURRENT = #{isNonconcurrent,jdbcType=VARCHAR},
  IS_UPDATE_DATA = #{isUpdateData,jdbcType=VARCHAR},
  REQUESTS_RECOVERY = #{requestsRecovery,jdbcType=VARCHAR},
  JOB_DATA = #{jobData,jdbcType=BLOB}
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}

update qrtz_job_details
set DESCRIPTION = #{description,jdbcType=VARCHAR},
  JOB_CLASS_NAME = #{jobClassName,jdbcType=VARCHAR},
  IS_DURABLE = #{isDurable,jdbcType=VARCHAR},
  IS_NONCONCURRENT = #{isNonconcurrent,jdbcType=VARCHAR},
  IS_UPDATE_DATA = #{isUpdateData,jdbcType=VARCHAR},
  REQUESTS_RECOVERY = #{requestsRecovery,jdbcType=VARCHAR}
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}
